Scripts.OnClick:
 if ( IsShiftKeyDown() ) then 
					local ItemName, ItemLink = GetItemInfo("item:"..GS_Data[GetRealmName()].Players[GS_DisplayPlayer].Equip[self:GetID()]); ChatFrameEditBox:Insert(ItemLink); 
					end

Scripts.OnEnter:
local Name = tonumber(string.sub(self:GetName(), 9)) local count = 0 GameTooltip:SetOwner(self, "ANCHOR_Right") GameTooltip:ClearLines(); GameTooltip:AddLine(GearScore_GetInstanceTranslation(GS_InstanceOrder[Name])); --local StatString = GS_Data[GetRealmName()].Players[GS_DisplayPlayer].StatString local StatString = GS_Data[GetRealmName()]["CurrentPlayer"]["EXP"] if not StatString then return; end local TempTable = {} --for i,v in pairs(GS_AchInfo[GS_InstanceOrder[Name]]) do -- local InsertionValue = { ["i"] = i, ["v"] = v } -- table.insert(TempTable, InsertionValue ) --end --table.sort(TempTable, function(a, b) return a["i"] < b["i"] end )
for i,v in pairs(GS_AchInfo[GS_InstanceOrder[Name]]) do --if not (GS_BackString[i]) then print(i,v) end count = tonumber(string.sub(StatString, GS_BackString[i],GS_BackString[i])) if not count then count = 0; end GameTooltip:AddDoubleLine(count.." muertes ("..(count/0.05).."%)",GearScore_GetBossTranslation(v),1 - (count/5),0 + (count/5),0,1,1,1)
end GameTooltip:AddLine(" ") if ( GS_Settings["ShowHelp"] == 1 ) then GameTooltip:AddLine("Recuerda, este jugador podría ser un alter y no ser su experiencia real.", 1, 1, 1, 1, true); end GameTooltip:AddLine("*Nota: '5' significa 5 o más muertes.") GameTooltip:Show()

Scripts.OnHide:
if ( GS_DatabaseFrame.tooltip ) then GS_DatabaseFrame.tooltip:Hide(); end
-- Restaurar estado de pestañas al cerrar
GS_OptionsFrame:Hide(); 
GS_NotesFrame:Hide(); 
GS_ExPFrame:Hide();
GS_DefaultFrame:Show(); 
GS_GearFrame:Show();
PanelTemplates_SetTab(GS_DisplayFrame, 1);

